mapper.select:
select * from board
where cat_no = 2 and bo_status=1
order by bo_no desc;

select * from board
where cat_no = 1 and bo_status=1
order by bo_no desc;

select * from board
where cat_no= 4 and bo_status=1
order by bo_no desc;

update board
set bo_status = 0
where bo_no=#{bo_no}

select * from board
where cat_no= 3 and bo_status=1
order by bo_no desc;

update board
set bo_status = 0
where bo_no=#{bo_no}

update board
set bo_writerId = #{bo_writerId}, bo_title = #{bo_title}, bo_content = #{bo_content}
where bo_no=#{bo_no}
